import React, {useState, useEffect} from 'react'
import { Container, AppBar, Typography, Grow, Grid, Paper, TextField, Button } from "@material-ui/core";
import Posts from '../Posts/Posts.js';
import Form from '../Form/Form.js'
import { useDispatch } from 'react-redux';
import { getPosts } from '../../actions/posts.js';
import useStyles from './styles.js';
import Paginate from '../Pagination.jsx';
import { useHistory, useLocation, useNavigate } from 'react-router-dom';
import ChipInput from 'material-ui-chip-input';



function useQuery(){
    return new URLSearchParams(useLocation().search);
}

const Home = () => {

    const [currentId, setCurrentId] = useState(null);
    const classes= useStyles ();
    const dispatch= useDispatch();
    const query = useQuery();
    const navigate = useNavigate();
    const page = query.get('page') || 1;
    const searchQuery = query.get('searchQuery');
    const [search, setSearch] = useState('');
    const [tags, setTags] = useState([]);
    const handleAdd = (tag) => setTags([...tags, tag])
    const handleDelete = (tagToDelete) => setTags(tags.filter((tag)=> tag !== tagToDelete))

    useEffect ( () => {
        dispatch(getPosts());
    },[currentId, dispatch])

    const searchPost = () => {
        if (search.trim()) {

        }else{
            navigate('/')
        }
    }

    const handleKeyPress = (e) => {
        if(e.keyCode === 13){
            searchPost()
        }
    }

  return (
    <div>
        <Grow in>
                <Container maxWidth="xl">
                    <Grid container justify="space-between" alignItems="stretch" spacing={3} className={classes.gridContainer}>
                        <Grid item xs={12} sm={6} md={9}>
                            <Posts setCurrentId={setCurrentId}/>
                        </Grid>

                        <Grid item xs={12} sm={4} md={3}>
                        <AppBar className={classes.appBarSearch} position='static' color='inherit'>
                        <TextField name="search" variant='outlined' label="Search Memories" fullWidth value={search} onKeyPress={handleKeyPress} onChange={(e) => setSearch(e.target.value)}/>
                        <ChipInput 
                         style={{ margin: '10px 0'}}
                         value={tags}
                         onAdd={handleAdd}
                         onDelete={handleDelete}
                         label= "Search Tags"
                         variant='outlined'
                        />
                        <Button onClick={searchPost} className={classes.searchButton} color='primary'>Search</Button>
                        </AppBar>
                            <Form currentId={currentId} setCurrentId={setCurrentId}/>

                            <Paper className={classes.pagination} elevation={6}>

                            <Paginate/>
                            </Paper>
                        </Grid>

                    </Grid>
                </Container>
            </Grow>

    </div>
  )
}

export default Home
