import React, {useState} from 'react'
import { Avatar, Button, Paper, Grid, Typography, Container, TextField, FormGroup } from '@material-ui/core';
import useStyles from './styles.js';
import LockOutlinedIcon from '@material-ui/icons/LockOpenOutlined.js'
import Input from './input.js';
import { GoogleLogin } from '@react-oauth/google';
import Icon from './icon.js';
import { useDispatch } from 'react-redux';
import jwt_decode, { jwtDecode } from 'jwt-decode';
import {useHistory} from 'react-router-dom';



const Auth = () => {
    const classes = useStyles();
    const [showPassword, setShowPassword] = useState(false); 
    const [isSignup, setIsSignup] = useState(false);
    const dispatch = useDispatch();

    const handleShowPassword = () => setShowPassword((prevShowPassword) => !prevShowPassword);

    const handleSubmit = () => {

    }
    const handleChange = () => {

    }

    const switchMode = () => {
        setIsSignup((previsSignup)=> !previsSignup );
        handleShowPassword(false);

    };
    const googleSuccess =  async (res) =>  {
        console.log(jwtDecode(res.credential))
        const result = res;
        const token = res?.credential;

        try {
            dispatch({type: 'AUTH', data: {result, token}});
            history.push('/');

        } catch (error) {
            console.log(error);
        }
    };

    const googleFailuer = (error) => {
        console.log(error)
        console.log('Google SignIn was unsuccessful. Try again later');
    };
  return (
    <Container component="main" maxWidth="xs">
        <Paper className={classes.paper} elevation={3}>
            <Avatar className={classes.avatar}>
                <LockOutlinedIcon/>
            </Avatar>
            <Typography variant='h5'>{isSignup? 'Sign up': 'Sign in'}</Typography>
            <form className={classes.form} onSubmit={handleSubmit}>
                <Grid  container spacing={2}>
                    {
                        isSignup && (
                            <>

                            <Input name="firstName" label="First Name" handleChange = {handleChange} autoFocus half />

                            <Input name="lastName" label="Last Name" handleChange = {handleChange} half  />

                            </>


                        )
                    }

                        <Input name="email" label="Email Address" handleChange={handleChange} type="email"/>
                        <Input name="password" label="Password" handleChange={handleChange} type={showPassword? "text": "password"} handleShowPassword={handleShowPassword}/>
                        {isSignup && <Input name="confirmPassword" label="Repeat Password" handleChange={handleChange} type="password"/>}
                </Grid>

                <Button type="submit" fullWidth variant='contained' color='primary' className={classes.submit}> {isSignup ? 'Sign Up': 'Sign In'} </Button>

                <GoogleLogin
                    clientId="365553992329-don01c74hkhiotbo7ep8o145062ff8ks.apps.googleusercontent.com"
                    render = { (renderProps) => (
                        <Button className={classes.googleButton} color='primary' fullWidth onClick={renderProps.onClick} disabled={renderProps.disabled} startIcon={<Icon/>} variant="contained">
                            Google Sign In
                            </Button>
                    )
                    }
                    onSuccess={googleSuccess}
                    onFailue={googleFailuer}
                    cookiePolicy="single_host_origin"
                />

                <Grid container justify='flex-end'>
                    <Grid item>
                        <Button onClick={switchMode}>
                            {isSignup? "Already have an account? Sign In": "Don't have an account? Sign Up"}

                        </Button>

                    </Grid>

                </Grid>
            </form>
        </Paper>

    </Container>
  )
}

export default Auth
