import React, {useState, useEffect} from 'react'
import { AppBar , Avatar, Button, Toolbar, Typography } from '@material-ui/core'
import memories from "../../images/memories.png"
import useStyles from './styles.js'
import {Link} from 'react-router-dom'
import { jwtDecode } from 'jwt-decode';
import { useDispatch } from 'react-redux';
import { useNavigate, useLocation } from 'react-router-dom'

const Navbar = () => {
    const navigate = useNavigate();
    const classes = useStyles();
    const [user, setUser] = useState(JSON.parse(localStorage.getItem("profile")))
    const dispatch = useDispatch(); 
    const location = useLocation();


    const logout = () => {

        dispatch({type : 'LOGOUT' });
        navigate('/')
        setUser(null);

    }



    useEffect (()=> {
        const token = user?.token;
        setUser(JSON.parse(localStorage.getItem("profile")));
    }, [location])

    return(
        <AppBar className={classes.appBar} position="static" color="inherit">
            <div className={classes.brandContainer}>
            <Typography component={Link} className={classes.heading} variant="h2" align="center" > 
            Memories
        </Typography>
        <img className={classes.image} src={memories} alt="memories" height="60"/>
            </div>

            <Toolbar className={classes.toolbar}>
                {user ? (
                    <div className={classes.profile}>

                        <Avatar className={classes.purple} alt={jwtDecode(user?.result?.credential).name} src={jwtDecode(user?.result?.credential).imageUrl}>{jwtDecode(user?.result?.credential).name.charAt(0)}</Avatar>
                        <Typography className={classes.userName} variant='h6'> {jwtDecode(user?.result?.credential).name} </Typography>
                        <Button variant="contained" className={classes.logout} color="secondary" onClick={logout}>Logout </Button>
                    </div>

                ): (
                    <Button component={Link} to='/auth' variant='contained' color="primary"> Sign In </Button>
                )}

            </Toolbar>


    </AppBar>
);
}

export default Navbar;
