import * as api from "../api";
import {CREATE, FETCH_BY_SEARCH, UPDATE, DELETE, FETCH_ALL, LIKE} from '../constants/actionTypes.js'

export const getPosts = (page) => async (dispatch) => {
    const {data} = await api.fetchPosts(page);
        console.log(data);
    try {
        const {data} = await api.fetchPosts(page);
        console.log(data);
        dispatch( {type: FETCH_ALL, payload: data } );

    } catch (error) {
        if (error.response) {
            // The request was made and the server responded with a status code
            // that falls out of the range of 2xx
            console.log(`Error status: ${error.response.status}`);
            console.log(`Error data: ${error.response.data}`);
            console.log(`Error headers: ${error.response.headers}`);
        } else if (error.request) {
            // The request was made but no response was received
            console.log(`No response received for the request: ${error.request}`);
        } else {
            // Something happened in setting up the request that triggered an Error
            console.log('Error', error.message);
        }
        console.log(error.config);
    } 

}

export const getPostBySearch = (searchQuery) => async (dispatch) => {
    try {
        const {data: {data}} = await api.fetchPostsBySearch(searchQuery);
        dispatch( {type: FETCH_BY_SEARCH, payload: data } );
    } catch (error) {
        console.log(error);
    }
}

export const createPost = (post) => async (dispatch) => {
    try {
        const {data} = await api.createPost(post);
        dispatch( {type: CREATE, payload: data } );
    } catch (error) {
        if (error.response && error.response.data) {
            console.log(error.response.data); // More detailed error message from the server
        } else {
            console.log(error.message);
        }
    }
}

export const updatePost = (id, post) => async (dispatch) => {
    try {
      const { data } = await api.updatePost(id, post);
      dispatch({ type: UPDATE, payload: data });
    } catch (error) {

        console.log(id, post);

      console.log(error.message);

    }
  };

export const deletePost = (id) => async (dispatch) => {
    try {
        await api.deletePost(id);
        dispatch({ type: DELETE, payload: id });

    } catch (error) {
        console.log(error);

    }
}

export const likePost = (id) => async (dispatch) => {
    try {

    const { data } = await api.likePost(id);
      dispatch({ type: LIKE, payload: data });

    } catch (error) {

    }
}
