import {AUTH, LOGOUT} from '../constants/actionTypes.js';

const authReducer = (state ={authData: null}, action) => {
    switch (action.type) {
        case AUTH:
            //console.log('AUTH action dispatched with data:', action.data); // Add this line to check if the action is dispatched correctly
            localStorage.setItem('profile', JSON.stringify({...action?.data}));
            //console.log('Data stored in localStorage:', localStorage.getItem('profile')); // Add this line to check if the data is stored in localStorage
            return {...state, authData: action?.data};

        default:
            return state;
    }
}

export default authReducer;
